const mongoose = require("mongoose");
mongoose.connect("mongodb://localhost:27017/db-koperasi-simpan-pinjam");

const pegawaiSchema = new mongoose.Schema({
    id_pegawai: Number,
    nama_peg: String,
    telp_peg: Number,
    gender: String,
   });
   const pegawai = mongoose.model("pegawai", pegawaiSchema);
   mongoose.pluralize(null);
   const fandi = new pegawai({
    id_pegawai: 2019230101 ,
    nama_peg: "Fandi Ahmad",
    telp_peg: 089695702303,
    gender: "Laki laki",
   });
   const bayu = new pegawai({
    id_pegawai: 20202300903 ,
    nama_peg: "Bayu Afif Nuranto",
    telp_peg: 081215430175,
    gender: "Laki laki",
   });
   const imam = new pegawai({
    id_pegawai: 2019230103 ,
    nama_peg: "Imam Fadhilah Rahman",
    telp_peg: 081917160541,
    gender: "Laki laki",
   });
   const raihan = new pegawai({
    id_pegawai: 2019230089 ,
    nama_peg: "Raihan Syah Putra Nasution",
    telp_peg: 081284343742,
    gender: "Laki laki",
   });


   //Mengecek apakah data sudah tersimpan atau error
    pegawai.insertMany(
    [fandi, bayu, imam, raihan],
    function (error) {
    if (error) {
    console.log(error);
    } else {
    console.log("Data Berhasil Disimpan!");
    mongoose.connection.close();
    }
    }
    );
